class s{constructor(t,e={}){this.navbar=t,this.options=Object.assign(e,{distance:0}),this.initialize()}addListeners(){document.addEventListener("scroll",()=>this.toggle())}toggle(){document.documentElement.scrollTop>this.options.distance?this.navbar.classList.add("scrolled"):this.navbar.classList.remove("scrolled")}initialize(){this.addListeners(),this.toggle()}static create(t){t.classList.contains("transparent-inited")||(new s(t),t.classList.add("transparent-inited"))}static init(){document.querySelectorAll(".navbar-transparent-top").forEach(t=>{s.create(t)})}}document.addEventListener("DOMContentLoaded",()=>{s.init()});
